import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './components/Login';
import DataEntry from './components/DataEntry';
import BalancePlanning from './components/BalancePlanning';
import Simulations from './components/Simulations';

function PrivateRoute({ element, allowedRoles }) {
  const token = localStorage.getItem('token');
  const userRole = localStorage.getItem('role');

  if (!token) {
    return <Navigate to="/login" />;
  }

  if (allowedRoles && !allowedRoles.includes(userRole)) {
    return <Navigate to="/" />;
  }

  return element;
}

function App() {
  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route
            path="/"
            element={<PrivateRoute element={<DataEntry />} />}
          />
          <Route
            path="/balance"
            element={<PrivateRoute element={<BalancePlanning />} allowedRoles={['admin', 'manager']} />}
          />
          <Route
            path="/simulaciones"
            element={<PrivateRoute element={<Simulations />} allowedRoles={['admin']} />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
