import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function DataEntry() {
    const [cultivos, setCultivos] = useState([]);
    const [nuevoCultivo, setNuevoCultivo] = useState({
        cultivo: '',
        especie: '',
        variedad: '',
        fecha: '',
        campo: '',
        lote: '',
        ubicacion: ''
    });

    useEffect(() => {
        // Aquí cargaríamos los cultivos existentes desde el backend
        // Por ahora, usaremos datos de ejemplo
        setCultivos([
        { id: 1, cultivo: 'Maíz', especie: 'Zea mays', variedad: 'Híbrido', fecha: '2024-08-01', campo: 'Campo Norte', lote: 'Lote 1', ubicacion: '34.6037° S, 58.3816° W' }
        ]);
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNuevoCultivo({ ...nuevoCultivo, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Aquí enviaríamos los datos al backend
        // Por ahora, solo lo añadiremos al estado local
        setCultivos([...cultivos, { id: cultivos.length + 1, ...nuevoCultivo }]);
        setNuevoCultivo({
        cultivo: '',
        especie: '',
        variedad: '',
        fecha: '',
        campo: '',
        lote: '',
        ubicacion: ''
        });
    };

    return (
        <div>
        <h1>Carga de Datos de Cultivos</h1>
        <form onSubmit={handleSubmit}>
            <input type="text" name="cultivo" value={nuevoCultivo.cultivo} onChange={handleInputChange} placeholder="Cultivo" required />
            <input type="text" name="especie" value={nuevoCultivo.especie} onChange={handleInputChange} placeholder="Especie" required />
            <input type="text" name="variedad" value={nuevoCultivo.variedad} onChange={handleInputChange} placeholder="Variedad" required />
            <input type="date" name="fecha" value={nuevoCultivo.fecha} onChange={handleInputChange} required />
            <input type="text" name="campo" value={nuevoCultivo.campo} onChange={handleInputChange} placeholder="Campo" required />
            <input type="text" name="lote" value={nuevoCultivo.lote} onChange={handleInputChange} placeholder="Lote" required />
            <input type="text" name="ubicacion" value={nuevoCultivo.ubicacion} onChange={handleInputChange} placeholder="Ubicación (Lat, Long)" required />
            <button type="submit">Agregar Cultivo</button>
        </form>

        <h2>Cultivos Registrados</h2>
        <ul>
            {cultivos.map(cultivo => (
            <li key={cultivo.id}>
                {cultivo.cultivo} - {cultivo.especie} - {cultivo.variedad} - {cultivo.fecha} - {cultivo.campo} - {cultivo.lote} - {cultivo.ubicacion}
                <Link to={`/cultivo/${cultivo.id}`}> Ver detalles</Link>
            </li>
            ))}
        </ul>
        </div>
    );
}

export default DataEntry;
