import React, { useState, useEffect } from 'react';

function CultivoDetail({ cultivoId }) {
    const [cultivo, setCultivo] = useState(null);
    const [riego, setRiego] = useState({ cantidad: '', tiempo: '' });
    const [precipitacion, setPrecipitacion] = useState('');
    const [observacion, setObservacion] = useState('');

    useEffect(() => {
        // Aquí cargaríamos los datos del cultivo desde el backend
        // Por ahora, usaremos datos de ejemplo
        setCultivo({
        id: cultivoId,
        cultivo: 'Maíz',
        especie: 'Zea mays',
        variedad: 'Híbrido',
        fecha: '2024-08-01',
        campo: 'Campo Norte',
        lote: 'Lote 1',
        ubicacion: '34.6037° S, 58.3816° W'
        });
    }, [cultivoId]);

    const handleRiegoChange = (e) => {
        const { name, value } = e.target;
        setRiego({ ...riego, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Aquí enviaríamos los datos al backend
        console.log('Datos de riego y precipitación:', { riego, precipitacion, observacion });
        // Resetear los campos después de enviar
        setRiego({ cantidad: '', tiempo: '' });
        setPrecipitacion('');
        setObservacion('');
    };

    if (!cultivo) return <div>Cargando...</div>;

    return (
        <div>
        <h2>Detalles del Cultivo: {cultivo.cultivo}</h2>
        <p>Especie: {cultivo.especie}</p>
        <p>Variedad: {cultivo.variedad}</p>
        <p>Fecha: {cultivo.fecha}</p>
        <p>Campo: {cultivo.campo}</p>
        <p>Lote: {cultivo.lote}</p>
        <p>Ubicación: {cultivo.ubicacion}</p>

        <h3>Agregar Información de Riego y Precipitaciones</h3>
        <form onSubmit={handleSubmit}>
            <label>
            Riego - Cantidad (mm):
            <input type="number" name="cantidad" value={riego.cantidad} onChange={handleRiegoChange} required />
            </label>
            <label>
            Riego - Tiempo (horas):
            <input type="number" name="tiempo" value={riego.tiempo} onChange={handleRiegoChange} required />
            </label>
            <label>
            Precipitación (mm):
            <input type="number" value={precipitacion} onChange={(e) => setPrecipitacion(e.target.value)} required />
            </label>
            <label>
            Observaciones:
            <textarea value={observacion} onChange={(e) => setObservacion(e.target.value)} />
            </label>
            <button type="submit">Guardar Información</button>
        </form>

        <h3>Datos Meteorológicos Automáticos</h3>
        <p>Esta sección se actualizará automáticamente con datos de las estaciones meteorológicas.</p>
        {/* Aquí iría la integración con las estaciones meteorológicas */}
        </div>
    );
}

export default CultivoDetail;
