import React from 'react';
import { Bar, Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, PointElement, LineElement, Title, Tooltip, Legend);

function Widget({ title, value, unit }) {
    return (
        <div className="widget" style={{ border: '1px solid #ddd', padding: '10px', margin: '10px', borderRadius: '5px', textAlign: 'center' }}>
        <h3>{title}</h3>
        <p style={{ fontSize: '24px', fontWeight: 'bold' }}>{value} {unit}</p>
        </div>
    );
    }

    function Simulations() {
    // Datos simulados para los gráficos
    const labels = ['Ene', 'Feb', 'Mar', 'Abr', 'May', 'Jun', 'Jul', 'Ago', 'Sep', 'Oct', 'Nov', 'Dic'];
    
    const aguaUtilData = {
        labels,
        datasets: [
        {
            label: 'Agua Útil',
            data: [65, 59, 80, 81, 56, 55, 40, 35, 30, 45, 55, 60],
            backgroundColor: 'rgba(53, 162, 235, 0.5)',
        },
        ],
    };

    const riegoLluviaData = {
        labels,
        datasets: [
        {
            type: 'bar',
            label: 'Riego',
            data: [20, 15, 10, 5, 25, 30, 35, 40, 35, 30, 25, 20],
            backgroundColor: 'rgba(75, 192, 192, 0.6)',
        },
        {
            type: 'bar',
            label: 'Lluvia',
            data: [30, 40, 35, 50, 45, 30, 25, 20, 25, 30, 35, 40],
            backgroundColor: 'rgba(54, 162, 235, 0.6)',
        },
        {
            type: 'line',
            label: 'Proyección de Agua',
            data: [80, 75, 90, 85, 70, 65, 60, 55, 50, 60, 70, 75],
            borderColor: 'rgb(255, 99, 132)',
            borderWidth: 2,
            fill: false,
        },
        ],
    };

    const options = {
        responsive: true,
        plugins: {
        legend: {
            position: 'top',
        },
        title: {
            display: true,
            text: 'Simulación de Agua, Riego y Lluvia',
        },
        },
    };

    return (
        <div>
        <h1>Simulaciones</h1>
        
        <div style={{ display: 'flex', justifyContent: 'space-around', flexWrap: 'wrap' }}>
            <Widget title="Valor de Agua" value="75" unit="mm" />
            <Widget title="Lluvias Acumuladas" value="250" unit="mm" />
            <Widget title="Humedad" value="65" unit="%" />
            <Widget title="Temperatura" value="22" unit="°C" />
        </div>

        <div style={{ width: '80%', margin: '20px auto' }}>
            <h2>Agua Útil en el Depósito</h2>
            <Bar data={aguaUtilData} options={options} />
        </div>

        <div style={{ width: '80%', margin: '20px auto' }}>
            <h2>Riego, Lluvia y Proyección de Agua</h2>
            <Bar data={riegoLluviaData} options={options} />
        </div>
        </div>
    );
}

export default Simulations;
